					</section>

					<section className="group bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm rounded-2xl p-8 border border-gray-200/50 dark:border-gray-700/50 hover:shadow-xl hover:shadow-green-500/10 dark:hover:shadow-emerald-500/10 transition-all duration-300 hover:-translate-y-1">
						<div className="flex items-center gap-4 mb-6">
							<div className="flex items-center justify-center w-12 h-12 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-xl">
								<Info className="w-6 h-6 text-white" />
							</div>
							<h2 className="text-2xl font-semibold text-gray-800 dark:text-gray-200">
								How to File a Complaint
							</h2>
						</div>
						<ol className="space-y-4">
							{[
								'Login to your account using your credentials',
								'Click "File a Complaint" from your dashboard',
								'Provide complete and accurate issue details',
								'Submit your complaint and track the status anytime',
							].map((step, index) => (
								<li key={index} className="flex items-start gap-4">
									<div className="flex items-center justify-center w-8 h-8 bg-gradient-to-r from-green-400 to-emerald-400 dark:from-green-500 dark:to-emerald-500 rounded-full text-white text-sm font-semibold flex-shrink-0 mt-0.5">
										{index + 1}
									</div>
									<span className="text-gray-600 dark:text-gray-400 leading-relaxed">{step}</span>
								</li>
							))}
						</ol>
					</section>

					<section className="group bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm rounded-2xl p-8 border border-gray-200/50 dark:border-gray-700/50 hover:shadow-xl hover:shadow-green-500/10 dark:hover:shadow-emerald-500/10 transition-all duration-300 hover:-translate-y-1">
						<div className="flex items-center gap-4 mb-6">
							<div className="flex items-center justify-center w-12 h-12 bg-gradient-to-r from-teal-500 to-green-500 rounded-xl">
								<Phone className="w-6 h-6 text-white" />
							</div>
							<h2 className="text-2xl font-semibold text-gray-800 dark:text-gray-200">
								Emergency Contacts
							</h2>
						</div>
						<div className="grid md:grid-cols-2 gap-4">
							{[
								{ name: 'Police', number: '100' },
								{ name: 'Fire', number: '101' },
								{ name: 'Women Helpline', number: '1091' },
								{ name: 'Child Helpline', number: '1098' },
								{ name: 'Cyber Crime', number: '155260' },
							].map((contact, index) => (
								<div
									key={index}
									className="flex items-center justify-between p-4 bg-gradient-to-r from-green-50 to-emerald-50 dark:from-gray-700/50 dark:to-emerald-900/20 rounded-xl border border-green-100 dark:border-gray-600/50"
								>
									<span className="font-medium text-gray-700 dark:text-gray-300">{contact.name}</span>
									<span className="font-bold text-green-600 dark:text-green-400 text-lg">
										{contact.number}
									</span>
								</div>
							))}
						</div>
					</section>

					<section className="group bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm rounded-2xl p-8 border border-gray-200/50 dark:border-gray-700/50 hover:shadow-xl hover:shadow-green-500/10 dark:hover:shadow-emerald-500/10 transition-all duration-300 hover:-translate-y-1">
						<div className="flex items-center gap-4 mb-4">
							<div className="flex items-center justify-center w-12 h-12 bg-gradient-to-r from-green-600 to-teal-600 rounded-xl">
								<FileText className="w-6 h-6 text-white" />
							</div>
							<h2 className="text-2xl font-semibold text-gray-800 dark:text-gray-200">
								Related Laws & Acts
							</h2>
						</div>
						<p className="text-gray-600 dark:text-gray-400 leading-relaxed">
							Access simplified summaries of local civic laws, nuisance acts, and safety regulations that govern
							citizen services and complaint procedures.
						</p>
					</section>

					<section className="group bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm rounded-2xl p-8 border border-gray-200/50 dark:border-gray-700/50 hover:shadow-xl hover:shadow-green-500/10 dark:hover:shadow-emerald-500/10 transition-all duration-300 hover:-translate-y-1">
						<div className="flex items-center gap-4 mb-6">
							<div className="flex items-center justify-center w-12 h-12 bg-gradient-to-r from-emerald-600 to-green-600 rounded-xl">
								<HelpCircle className="w-6 h-6 text-white" />
							</div>
							<h2 className="text-2xl font-semibold text-gray-800 dark:text-gray-200">
								Frequently Asked Questions
							</h2>
						</div>
						<div className="space-y-4">
							{faqData.map((item, index) => (
								<AnimatedAccordion
									key={index}
									item={item}
									index={index}
									isOpen={openIndex === index}
									onToggle={toggleFAQ}
								/>
							))}
						</div>
					</section>
				</div>
			</div>
		</div>
	);
};

export default Resources;

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
mimport { motion } from 'framer-motion';
import { toast, ToastContainer } from 'react-toastify';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [issues, setIssues] = useState([]);

  // Animation variants for the page container
  const pageVariants = {
    initial: { opacity: 0, y: 20 },
    animate: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut",
        staggerChildren: 0.1
      }
    },
    exit: {
      opacity: 0,
      y: -20,
      transition: { duration: 0.4 }
    }
  };

  // Animation variants for table rows
  const rowVariants = {
    initial: { opacity: 0, x: -20 },
    animate: {
      opacity: 1,
      x: 0,
      transition: { duration: 0.4 }
    }
  };

  // Fetch all reported issues
  const fetchIssues = React.useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('http://localhost:5001/api/issues', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!res.ok) {
        throw new Error('Unauthorized or failed to fetch');
      }

      const data = await res.json();
      setIssues(data);
    } catch (error) {
      console.error('Error fetching issues:', error);
      alert('Failed to fetch issues. Please login again.');
      navigate('/login');
    }
  }, [navigate]);

  // Update issue status
  const handleStatusChange = async (id, status) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5001/api/issues/${id}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });

      if (!res.ok) {
        throw new Error('Failed to update status');
      }

      // Refresh issue list
      fetchIssues();
    } catch (error) {
      console.error('Error updating status:', error);
      alert('Could not update status.');
    }
  };

  useEffect(() => {
    fetchIssues();
  }, [fetchIssues]);

  return (
    <motion.div
      className="min-h-screen bg-gray-50"
      variants={pageVariants}
      initial="initial"
      animate="animate"
      exit="exit"
    >
      {/* Issues Table */}
      <main className="p-6">
        <motion.h2
          className="text-xl font-bold mb-4"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.1 }}
        >
          Admin Dashboard
        </motion.h2>
        <motion.p
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          Welcome, Admin! You have access to admin controls.
        </motion.p>
        <ToastContainer
          position="top-right"
          autoClose={3000}
          closeOnClick
          pauseOnHover
          draggable
          theme="dark"
          toastClassName="toast-body custom-toast-shadow"
          bodyClassName="text-sm font-medium"
        />
        <motion.h3
          className="text-lg font-semibold mb-4"
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
        >
          Reported Issues
        </motion.h3>

        {issues.length === 0 ? (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            No issues found.
          </motion.p>
        ) : (
          <motion.div
            className="overflow-x-auto"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            <table className="min-w-full border border-gray-300 bg-white text-sm rounded-lg overflow-hidden shadow">
              <thead className="bg-gray-200 text-gray-700">
                <motion.tr
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.5, delay: 0.5 }}
                >
                  <th className="p-3">Title</th>
                  <th className="p-3">Description</th>
                  <th className="p-3">Phone</th>
                  <th className="p-3">Email</th>
                  <th className="p-3">Status</th>
                  <th className="p-3">Change Status</th>
                </motion.tr>
              </thead>
              <tbody>
                {issues.map((issue, index) => (
                  <motion.tr
                    key={issue._id}
                    className="border-t border-gray-200"
                    variants={rowVariants}
                    initial="initial"
                    animate="animate"
                    transition={{ delay: 0.6 + (index * 0.1) }}
                  >
                    <td className="p-3">{issue.title}</td>
                    <td className="p-3">{issue.description}</td>
                    <td className="p-3">{issue.phone}</td>
                    <td className="p-3">{issue.email}</td>
                    <td className="p-3">{issue.status || 'Pending'}</td>
                    <td className="p-3">
                      <select
                        value={issue.status || 'Pending'}
                        onChange={(e) => handleStatusChange(issue._id, e.target.value)}
                        className="border border-gray-300 rounded px-2 py-1"
                      >
                        <option>Pending</option>
                        <option>In Progress</option>
                        <option>Resolved</option>
                        <option>Rejected</option>
                      </select>
                    </td>
                  </motion.tr>
                ))}
              </tbody>
            </table>
          </motion.div>
        )}
      </main>
    </motion.div>
  );
};

export default AdminDashboard;

